'use strict'

/**
 * Module exports.
 * @public
 */

export function forwarded(req: Request): string[] {
  if (!req) {
    throw new TypeError('argument req is required')
  }

  let proxyAddrs = parse(req.headers['x-forwarded-for'] || '')
  let socketAddr = getSocketAddr(req)
  let addrs = [socketAddr, ...proxyAddrs]

  return addrs
}

/**
 * Get the socket address for a request.
 *
 * @param {object} req
 * @return {string}
 * @private
 */

function getSocketAddr(req: Request): string {
  return req.socket
    ? req.socket.remoteAddress
    : req.connection.remoteAddress
}

/**
 * Parse the X-Forwarded-For header.
 *
 * @param {string} header
 * @private
 */

function parse(header: string): string[] {
  let end = header.length
  let list: string[] = []
  let start = header.length

  // gather addresses, backwards
  for (let i = header.length - 1; i >= 0; i--) {
    switch (header.charCodeAt(i)) {
      case 0x20: /*   */
        if (start === end) {
          start = end = i
        }
        break
      case 0x2c: /* , */
        if (start !== end) {
          list.push(header.substring(start, end))
        }
        start = end = i
        break
      default:
        start = i
        break
    }
  }

  // final address
  if (start !== end) {
    list.push(header.substring(start, end))
  }

  return list
}
