const utf7 = require('./utf7');

const codec = new utf7.Utf7Codec();

const encoded = codec.encoder.write('Hello, World!');
const decoded = codec.decoder.write(encoded);

console.log(decoded); // 'Hello, World!'



const utf7imap = require('./utf7');

const codec = new utf7imap.Utf7IMAPCodec();

const encoded = codec.encoder.write('Hello, World!');
const decoded = codec.decoder.write(encoded);

console.log(decoded); // 'Hello, World!'

